INSERT INTO `wp_ppress_formsmeta` VALUES (215,1,'melange','form_structure','s:1938:\"<div class=\"lucidContainer\">\n	<div class=\"multitab-section\">\n		<ul class=\"multitab-widget multitab-widget-content-tabs-id\">\n			<li class=\"multitab-tab\"><a href=\"#lucidLogin\">Log In</a></li>\n			<li class=\"multitab-tab\"><a href=\"#lucidRegistration\">Register</a></li>\n			<li class=\"multitab-tab\"><a href=\"#lucidReset\">Reset</a></li>\n		</ul>\n		<div class=\"multitab-widget-content multitab-widget-content-widget-id\" id=\"lucidLogin\">\n      <span id=\"sidebartab1\">\n        [pp-login-form]\n		[login-username placeholder=\"Username\"]\n		[login-password placeholder=\"Password\"]\n		[login-submit value=\"Log In\"]\n		[/pp-login-form]\n      </span>\n		</div>\n		<div class=\"multitab-widget-content multitab-widget-content-widget-id\" id=\"lucidRegistration\">\n      <span id=\"sidebartab2\">\n		[pp-registration-form]\n		[reg-username placeholder=\"Username\"]\n		[reg-email placeholder=\"Email Address\"]\n		[reg-password placeholder=\"Password\"]\n		[reg-submit value=\"Register\"]\n		[/pp-registration-form]\n      </span>\n		</div>\n		<div class=\"multitab-widget-content multitab-widget-content-widget-id\" id=\"lucidReset\">\n      <span id=\"sidebartab3\">\n        [pp-password-reset-form]\n		[user-login value=\"Username or Email\"]\n		[reset-submit value=\"Get New Password\"]\n		[/pp-password-reset-form]\n      </span>\n		</div>\n	</div>\n</div>\n\n<script type=\"text/javascript\">\n	jQuery(document).ready(function ($) {\n		$(\".multitab-widget-content-widget-id\").hide();\n		$(\"ul.multitab-widget-content-tabs-id li:first a\").addClass(\"multitab-widget-current\").show();\n		$(\".multitab-widget-content-widget-id:first\").show();\n		$(\"ul.multitab-widget-content-tabs-id li a\").on(\'click\', function () {\n			$(\"ul.multitab-widget-content-tabs-id li a\").removeClass(\"multitab-widget-current a\");\n			$(this).addClass(\"multitab-widget-current\");\n			$(\".multitab-widget-content-widget-id\").hide();\n			var activeTab = $(this).attr(\"href\");\n			$(activeTab).fadeIn();\n			return false;\n		});\n	});\n</script>\";');
INSERT INTO `wp_ppress_formsmeta` VALUES (216,1,'melange','form_css','s:3865:\"@import url(https://fonts.googleapis.com/css?family=Open+Sans:300,400,600,700&display=swap);\n\n/* css class for login form generated errors */\n.profilepress-login-status {\n  border-radius: 5px;\n    max-width: 350px;\n    font-size: 15px;\n    line-height: 1.471;\n    padding: 10px;\n    background-color: #e74c3c;\n    color: #ffffff;\n    font-weight: normal;\n    display: block;\n    vertical-align: middle;\n    margin: 5px 0;\n}\n\n.profilepress-login-status a {\n    color: #fff;\n    text-decoration: underline;\n}\n\n/* css class for registration form generated errors */\n.profilepress-reg-status {\n  border-radius: 5px;\n    max-width: 350px;\n    font-size: 15px;\n    line-height: 1.471;\n    padding: 10px;\n    background-color: #e74c3c;\n    color: #ffffff;\n    font-weight: normal;\n    display: block;\n    vertical-align: middle;\n    margin: 5px 0;\n}\n\n.profilepress-reset-status a {\n    color: #fff;\n    text-decoration: underline;\n}\n\n/* css class for password reset form generated errors */\n.profilepress-reset-status {\n  border-radius: 5px;\n    max-width: 350px;\n    font-size: 15px;\n    line-height: 1.471;\n    padding: 10px;\n    background-color: #e74c3c;\n    color: #ffffff;\n    font-weight: normal;\n    display: block;\n    vertical-align: middle;\n    margin: 5px 0;\n}\n\n.profilepress-reset-status a {\n    color: #fff;\n    text-decoration: underline;\n}\n\n.lucidSuccess {\n  border-radius: 5px;\n  max-width: 350px;\n  font-size: 15px;\n  line-height: 1.471;\n  padding: 10px;\n  background-color: #2ecc71;\n  color: #ffffff;\n  font-weight: normal;\n  display: block;\n  margin-top: 5px;\n  margin-bottom: 5px;\n}\n\n.lucidContainer {\n  max-width: 350px;\n  margin: 30px 0;\n  padding: 0;\n  box-shadow: 0 10px 5px -5px rgba(0, 0, 0, 0.1);\n  font-family: \'Open Sans\', sans-serif;\n}\n\n/* Multi Tab Sidebar */\n\n.multitab-section {\n  display: inline-block;\n  text-transform: uppercase;\n  width: 100%;\n}\n\n.multitab-section p {\n  display: inline-block;\n  background: #fff;\n  text-transform: lowercase;\n  font-size: 14px;\n  padding: 20px;\n  margin: 0;\n}\n\n.multitab-widget {\n  list-style: none;\n  margin: 0 0 10px;\n  padding: 0\n}\n\n.multitab-widget li {\n  list-style: none;\n  padding: 0;\n  margin: 0;\n  float: left\n}\n\n.multitab-widget li a {\n  background: #22a1c4;\n  color: #fff;\n  display: block;\n  padding: 15px;\n  font-size: 13px;\n  text-decoration: none\n}\n\n.multitab-tab {\n  width: 33.3%;\n  text-align: center\n}\n\n.multitab-section h2,\n.multitab-section h3,\n.multitab-section h4,\n.multitab-section h5,\n.multitab-section h6 {\n  display: none;\n}\n\n.multitab-widget li a.multitab-widget-current {\n  padding-bottom: 20px;\n  margin-top: -10px;\n  background: #fff;\n  color: #444;\n  text-decoration: none;\n  border-top: 5px solid #22a1c4;\n  font-size: 14px;\n  text-transform: capitalize\n}\n\n.multitab-widget-content {\n  padding: 0 20px;\n  border-bottom: 1px solid #efefef;\n  border-left: 1px solid #efefef;\n  border-right: 1px solid #efefef;\n}\n\n\ndiv.lucidContainer input[type=\"email\"],\ndiv.lucidContainer input[type=\"text\"],\ndiv.lucidContainer input[type=\"password\"], div.lucidContainer select, div.lucidContainer textarea {\n  width: 100%;\n  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.125);\n  box-sizing: border-box;\n  -webkit-box-sizing: border-box;\n  -moz-box-sizing: border-box;\n  background: #fff;\n  margin: 10px auto;\n  border: 1px solid #ccc;\n  padding: 10px;\n  font-family: \'Open Sans\', sans-serif;\n  font-size: 95%;\n  color: #555;\n}\n\n\ndiv.lucidContainer input[type=\"submit\"] {\n  width: 100%;\n  box-sizing: border-box;\n  -webkit-box-sizing: border-box;\n  -moz-box-sizing: border-box;\n  margin: 10px auto;\n  background: #3399cc;\n  border: 0;\n  padding: 4%;\n  font-family: \'Open Sans\', sans-serif;\n  font-size: 100%;\n  color: #fff;\n  cursor: pointer;\n  transition: background .3s;\n  -webkit-transition: background .3s;\n}\n\ndiv.lucidContainer input[type=\"submit\"]:hover {\n  background: #2288bb;\n}\n\n\";');
